SELECT customer.memberID, return_transaction.transactionID, furniture.furnitureId, style.label as `Style`, category.label as `Category Name`, rental_transaction.transactionId, rental_transaction.dueDate, rental_transaction.transactionDate, return_transaction.returnDate, Concat(employee.firstName, ' ', employee.lastName) as Employee FROM customer, rental_transaction, rental_item, furniture, style, category, return_transaction, employee WHERE memberID = rental_transaction.customerID and memberID = 9 and rental_transaction.transactionID = rental_item.rentalID and furniture.furnitureId = rental_item.furnitureId and style.styleId = furniture.styleId and category.categoryId = furniture.categoryId and memberID = return_transaction.customerID and employee.employeeId = return_transaction.employeeId

SELECT memberID, Concat(customer.firstname, " ", customer.lastname) as `member name`, rental_transaction.transactionDate, rental_transaction.dueDate, COUNT(rental_item.quantity) as `Items rented` from customer join rental_transaction on memberID = rental_transaction.customerId, rental_item where rental_item.rentalId = rental_transaction.transactionId GROUP BY memberID, rental_transaction.transactionDate, rental_transaction.dueDate HAVING COUNT(rental_item.quantity) >= 2